                if(!String.prototype.shuffle)
                {
                    String.prototype.shuffle = function () {
                        var a = this.split(""),
                            n = a.length;

                        for(var i = n - 1; i > 0; i--) {
                            var j = Math.floor(Math.random() * (i + 1));
                            var tmp = a[i];
                            a[i] = a[j];
                            a[j] = tmp;
                        }
                        return a.join("");
                    }
                }
                // Few prototype entries
                // Add a method "has" to array
                Array.prototype.has = function (value) {
                    var i;
                    for (var i = 0, loopCnt = this.length; i < loopCnt; i++) {
                        if (this[i] === value) {
                            return true;
                        }
                    }
                    return false;
                };
                //Add "indexOf" method to array
                if (!Array.prototype.indexOf) {
                    Array.prototype.indexOf = function (value) {
                        var i;
                        for (var i = 0, loopCnt = this.length; i < loopCnt; i++) {
                            if (this[i] === value) {
                                return i;
                            }
                        }
                        return -1;
                    };
                }
                if (!Array.prototype.humanSort) {
                    Array.prototype.naturalSort = function() {
                      return this.sort(function(a, b) {
                        aa = a.name.split(/(\d+)/);
                        bb = b.name.split(/(\d+)/);

                        for(var x = 0; x < Math.max(aa.length, bb.length); x++) {
                          if(aa[x] != bb[x]) {
                            var cmp1 = (isNaN(parseInt(aa[x],10)))? aa[x] : parseInt(aa[x],10);
                            var cmp2 = (isNaN(parseInt(bb[x],10)))? bb[x] : parseInt(bb[x],10);
                            if(cmp1 == undefined || cmp2 == undefined)
                              return aa.length - bb.length;
                            else
                              return (cmp1 < cmp2) ? -1 : 1;
                          }
                        }
                        return 0;
                      });
                    }
                }
                // Array Remove - By John Resig (MIT Licensed)
                Array.prototype.remove = function (from, to) {
                    if (this.slice) {
                        var rest = this.slice((to || from) + 1 || this.length);
                        this.length = from < 0 ? this.length + from : from;
                        return this.push.apply(this, rest);
                    } else {
                        return this;
                    }
                }

                //Add Filter method in array if not available
                if (!('filter' in Array.prototype)) {
                    Array.prototype.filter= function(filter, that /*opt*/) {
                        var other= [], v;
                        for (var i=0, n= this.length; i<n; i++)
                            if (i in this && filter.call(that, v= this[i], i, this))
                                other.push(v);
                        return other;
                    };
                }
                //Clean array, usage array.clean("");
                Array.prototype.clean = function(deleteValue) {
                  for (var i = 0; i < this.length; i++) {
                    if (this[i] == deleteValue) {
                      this.splice(i, 1);
                      i--;
                    }
                  }
                  return this;
                };

                //Array diff operation
                Array.prototype.diff = function(a) {
                    return this.filter(function(i) {return !(a.indexOf(i) > -1);});
                };

                //Finds average numeric value in an array
                Array.prototype.average = function() {
                    var av = 0;
                    var cnt = 0;
                    var len = this.length;
                    for (var i = 0; i < len; i++) {
                        var e = +this[i];
                        if(!e && this[i] !== 0 && this[i] !== '0') e--;
                        if (this[i] == e) {av += e; cnt++;}
                    }
                    return av/cnt;
                }

                Number.prototype.leftZeroPad = function(numZeros) {
                    var n = Math.abs(this);
                    var zeros = Math.max(0, numZeros - Math.floor(n).toString().length );
                    var zeroString = Math.pow(10,zeros).toString().substr(1);
                    if( this < 0 ) {
                            zeroString = '-' + zeroString;
                    }
                    return zeroString+n;
                }

                $.expr[":"].exacttext = function(obj, index, meta, stack){
                    return (obj.textContent || obj.innerText || $(obj).text() || "").toLowerCase() == meta[3].toLowerCase();
                }

                String.prototype.replaceAll = function(token, newToken, ignoreCase) {
                    var str, i = -1, _token;
                    if((str = this.toString()) && typeof token === "string") {
                        _token = ignoreCase === true? token.toLowerCase() : undefined;
                        while((i = (
                            _token !== undefined?
                                str.toLowerCase().indexOf(
                                            _token,
                                            i >= 0? i + newToken.length : 0
                                ) : str.indexOf(
                                            token,
                                            i >= 0? i + newToken.length : 0
                                )
                        )) !== -1 ) {
                            str = str.substring(0, i)
                                    .concat(newToken)
                                    .concat(str.substring(i + token.length));
                        }
                    }
                return str;
                };

