var loading = {
    init : function(){
        this.panel = $("#loadingIndicator").hide();
        if(this.panel.dialog("instance"))
            return;
        this.panel.dialog({
            autoOpen: false,
            dialogClass: "loadingIndicatorWindow",
            closeOnEscape: false,
            draggable: false,
            width: window.loadingIndicatorDialogWidth || 150,
            minHeight: 50,
            modal: false,
            buttons: {},
            resizable: false,
            open: function() {
                $('body').css('overflow','hidden');
            },
            close: function() {
                $('body').css('overflow','auto');
                $(this).dialog('destroy');
            }
        });
    },
    shown : false,
    count : 0,
    panel : $("#loadingIndicator"),
    modal : false,
    show : function(modal){
        this.init();
        if(modal && this.panel.dialog){
            this.modal = modal;
            this.panel.dialog("option", "modal", true);
        }
        if(!this.shown && this.panel.dialog){
            this.panel.dialog('open');
            this.shown = true;
        }
        this.count++;
    },
    hide : function(force){
        this.count--;
        this.count = this.count<0 ? 0 : this.count;
        if(force)
            this.count = 0;
        if(this.count == 0 && this.panel.dialog){
            try{
                if(!this.panel.dialog("instance"))
                    return;
                var that = this;
                setTimeout(function(){
                    that.init();
                    that.panel.dialog("option", "modal", false);
                    //that.panel.dialog('close');
                })
                this.shown = false;
                var that = this;
                setTimeout(function(){
                    if(!that.shown){
                        that.init();
                        if(that.panel.dialog("instance")){
                            that.panel.dialog("option", "modal", false);
                            that.panel.dialog('close');
                            that.panel.hide();
                        }
                    }
                }, 500);
            }catch(ex){}
        }
    }
};